import axios from "axios";
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Link } from "react-router-dom";

const TodoForm = () => {
  const [todoValue, setTodoValue] = useState("");
  const [todos, setTodos] = useState([]);

  // Sets the value of the todo
  const handleInputChange = (e) => {
    setTodoValue(e.target.value);
  };

  const getTodos = async () => {
    const response = await axios.get("/todos");
    setTodos(response.data);
  };

  useEffect(() => {
    getTodos();
  }, []);

  // Handles submit to make post request to database
  const handleSubmit = (e) => {
    /**
     * Reset input field
     * Add edit button using app.put
     */

    if (todoValue !== "") {
      e.preventDefault();
      const todo = {
        title: todoValue,
      };

      // axios post request to backend
      axios
        .post("/add/todo", todo)
        .then(() => getTodos())
        .catch((err) => console.error(err));
    } else {
      console.log("error");
    }
  };

  const handleDelete = (id) => {
    axios
      .delete(`/delete/todo/${id}`)
      .then(() => getTodos())
      .catch((err) => console.error(err));
  };

  return (
    <Router className="container mt-5">
      <form className=" d-flex flex-row mb-3" onSubmit={handleSubmit}>
        <input
          type="text"
          className="form-control me-3"
          placeholder="Todo"
          onChange={handleInputChange}
        />
        <input type="submit" value="Add Todo" className="btn btn-primary" />
      </form>

      <div>
        <ul className="list-group">
          {todos.map((todo) => (
            <li
              key={todo._id}
              className="list-group-item d-flex justify-content-between align-items-center"
            >
              {todo.title}
              <div>
                <Link to="/edit">
                  <button className="btn btn-secondary">Edit</button>
                </Link>
                <button
                  className="btn btn-danger ms-3"
                  onClick={() => handleDelete(todo._id)}
                >
                  ðŸ—‘
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </Router>
  );
};

export default TodoForm;
