import React, { useState, useEffect } from "react";
import axios from "axios";

const Todos = () => {
  const [todos, setTodos] = useState([]);

  const getTodos = () => {
    axios
      .get("/todos")
      .then((res) => setTodos(res.data))
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    getTodos
  }, [todos]);

  return (
    <div>
      <ul>
        {todos.map((todo) => (
          <li key={todo.id}>{todo.title}</li>
        ))}
      </ul>
    </div>
  );
};

export default Todos;
